import React from "react";

// There are two of these in the app,
// one for setting the focus duration
// and one for setting the break duration
function DurationControl({ dataTestIdEnding, float }) {
    const mode = titleCase(dataTestIdEnding);
    return (
        <div className={`float-${float}`}>
            <div className="input-group input-group-lg mb-2">
                <span className="input-group-text" data-testid={`duration-${dataTestIdEnding}`}>
                {/* TODO: Update this text to display the current focus or break session duration */}
                {mode} Duration: 25:00
                </span>
                <div className="input-group-append">
                {/* TODO: Implement decreasing focus and break duration and disable during a focus or break session */}
                <button
                    type="button"
                    className="btn btn-secondary"
                    data-testid={`decrease-${dataTestIdEnding}`}
                >
                    <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing focus and break duration and disable during a focus or break session */}
                <button
                    type="button"
                    className="btn btn-secondary"
                    data-testid={`increase-${dataTestIdEnding}`}
                >
                    <span className="oi oi-plus" />
                </button>
                </div>
            </div>
        </div>
    )
}

function titleCase(str) {
    return str.toLowerCase().split(' ').map((word) => {
        return (word.charAt(0).toUpperCase() + word.slice(1));
    }).join(' ');
}

export default DurationControl;
